import React from 'react';
import { Outlet } from 'react-router-dom';
import "./style.css"
import food from '../ComponentCart/Products.json';

const Offer = () => {
  return (
    <>
      <section id="offer">

        <h1>OUR <span>Offers</span></h1>


        <div id="box-container">
          {food.map((val)=>{
          <div className="box">
          <img src="https://www.thespruceeats.com/thmb/yLyKgqIj6-SLt9gJkgkjfIZtfC0=/4300x2867/filters:no_upscale():max_bytes(150000):strip_icc()/chole-chickpea-curry-1957946-hero-01-cddf957cc78d4032ba29ec2adbc05a97.jpg" alt="" />
          <h3>{val.filename} </h3>
          <div className="price">₹{val.price} <span>₹{val.price}+50</span></div>
          <button className="btn"> Add to Cart</button>
        </div>
        })}
        </div>
      </section>
      <Outlet/>
    </>
  )
}

export default Offer
