import React from 'react';
import './cart.css';
import product from './Products.json'

const Cart = ({ cart, cartSize }) => {
  return (
    <>
      <div id='itemContainer'>
        {product.map((val) => {
          if (val.cart >= 1)
            return
          <div id='cartContainer'>
            <div className="cartitemImg">
              <img src="../Images/`${val.filename}" alt={val.title} />
              <button>-</button><input placeholder={val.cart} /><button>+</button>
            </div>
            < div id='itemDetails' >
              <h3>{val.title}</h3>
              <p>{val.description}</p>
              <h5>{val.price}</h5>
            </div >
          </div>
        })}

      </div>
      <div id='placeOrder'><button>Place Order</button></div>
    </>
  )
}

export default Cart
