import React, { useState } from 'react';
import './cart.css';
import product from './Products.json'

const Cart = ({ cart, cartSize }) => {

  var [arr, setArr] = useState(product);
  const add = (e) => {
    const name = e.target.closest("#cartContainer").className;
    product.forEach(val => {
      if (val.title.trim().toLowerCase() === name.trim().toLowerCase()) {
        val.cart++;
      }
    })
    setArr([...product]);
  }
  const sub = (e) => {
    const name = e.target.closest("#cartContainer").className;
    product.forEach(val => {
      if (val.title.trim().toLowerCase() === name.trim().toLowerCase()) {
        if (val.cart <= 1)
          val.cart = 1;
        else
          val.cart--;



        setArr([...product]);
      }
    })


  }

  const remove =(e)=>{
    const name = e.target.closest("#cartContainer").className;
    product.forEach(val => {
      if (val.title.trim().toLowerCase() === name.trim().toLowerCase()) {
        val.cart=0;
        setArr([...product]);
      }
    })
  }

  return (
    <>
    <div id="containerAll">

    </div>

      {/* <div id='placeOrder'><button>Place Order</button></div> */}
    </>
  )
}

export default Cart
