import React, { useState } from 'react';
import './cart.css';
import product from './Products.json'

const Cart = ({ cart, cartSize }) => {

  var [arr, setArr] = useState(product);
  var [totalPrice, setTotalPrice] = useState(0);

  const add = (e) => {
    const name = e.target.closest("#cartContainer").className;
    product.forEach(val => {
      if (val.title.trim().toLowerCase() === name.trim().toLowerCase()) {
        val.cart++;
      }
    })
    setArr([...product]);
  }

  const sub = (e) => {
    const name = e.target.closest("#cartContainer").className;
    product.forEach(val => {
      if (val.title.trim().toLowerCase() === name.trim().toLowerCase()) {
        if (val.cart <= 1)
          val.cart = 1;
        else
          val.cart--;
      }
      setArr([...product]);
    })
  }

  const remove = (e) => {
    const name = e.target.closest("#cartContainer").className;
    product.forEach(val => {
      if (val.title.trim().toLowerCase() === name.trim().toLowerCase()) {
        val.cart = 0;
        setArr([...product]);
      }
    })
  }

  const empty = () => {
    product.forEach(val => {
      val.cart = 0;
    })
    setArr([...product]);
  }

  return (
    <>
      <div id="containerAll">
        <div id='itemContainer'>
          <h1> Cart Details</h1>
          {arr.map((val) => {
            if (val.cart >= 1)
              return (
                <div id='cartContainer' className={val.title}>
                  <div className="cartitemImg">
                    <img src={require(`../Images/${val.filename}`)} alt={val.title} />
                    <div><span onClick={sub} className="minus">&#8722;</span><input placeholder={val.cart} disabled /><span onClick={add} className="plus">	&#43;</span></div>
                  </div>
                  < div id='itemDetails' >
                    <h3>{val.title}</h3>
                    <p>{val.description}</p>
                    <h5>Price : â‚¹{(val.price * val.cart).toFixed(1)}</h5>
                    <div className="extra" onClick={remove}><h6>Remove</h6></div>
                  </div >
                </div>
              )
          })}
        </div>


        <div id='totalDisplay'>
          <h1>Price Details</h1>
          <div>
            <div style={{ display: "flex", justifyContent: "space-between", width: "80%", margin: "auto" }}><h2>Price ( {cartSize}  items ) </h2> <span>{totalPrice}</span></div>
            <div style={{ display: "flex", justifyContent: "space-between", width: "80%", margin: "auto", marginTop: "8vh" }}>  <h2>Discont</h2> <span>{totalPrice <= 0 ? totalPrice : (totalPrice - 50)}</span></div>
          </div>
          <button id='empty' onClick={empty}>Empty Cart</button>

          <div style={{ display: "flex", justifyContent: "space-between", width: "80%", margin: "auto", marginTop: "8vh" }}><h2 style={{color:"tomato"}}>Total Amount</h2><span style={{fontSize:"3rem"}}>{totalPrice}</span></div>
        </div>

      </div>

      {/* <div id='placeOrder'><button>Place Order</button></div> */}
    </>
  )
}

export default Cart
