import React, { useState } from 'react';
import { Outlet } from 'react-router-dom';
import "./style.css"
import food from '../ComponentCart/Products.json';
import cartItem from '../ComponentCart/Cartitem.json'


const Offer = ({cartSize,setCartSize}) => {

  const addToCart = (e) => {
    const item = e.target.parentNode.id;
    food.map(val=>{
      if(val.title === item)
      {
        const obj = val;
        // const quantity  = 1;
        // const name = val.title;
        // const price = val.price;
        // const filename = val.filename;
        // const desc = val.description;
        // const obj = {"filename":filename,"name":name,"quantity":quantity,"price":price,"desc":desc};
        cartItem.push(obj);
        console.log(cartItem);
        setCartSize
      }

    })    
  }

  return (
    <>
      <section id="offer">
        <h1>OUR <span>Offers</span></h1>
        <div id="box-container">
          {food.map((val,index) => {
            return <><div className="box" id={val.title} key={index}>
              <img src={require(`../Images/${val.filename}`)} alt="product_image" />
              <h3>{val.title} </h3>
              <div className="price">₹{val.price} <span>₹{Number(val.price) + 50}</span></div>
              <button className="btn" onClick={addToCart}> Add to Cart</button>
            </div></>
          })}
        </div>
      </section>
      <Outlet />
    </>
  )
}

export default Offer
