import React, { useState } from 'react';
import './cart.css';
import product from './Products.json'

const Cart = ({ cart, cartSize }) => {

  var [arr, setArr] = useState(product);
  const add = (e) => {
    const name = e.target.closest("#cartContainer").className;
    product.forEach(val => {
      if (val.title.trim().toLowerCase() === name.trim().toLowerCase()) {
        val.cart++;
      }
    })
    setArr([...product]);
  }
  const sub = (e) => {
    const name = e.target.closest("#cartContainer").className;
    product.forEach(val => {
      if (val.title.trim().toLowerCase() === name.trim().toLowerCase()) {
        if (val.cart <= 1)
          val.cart = 1;
        else
          val.cart--;



        setArr([...product]);
      }
    })


  }

  const remove =(e)=>{
    const name = e.target.closest("#cartContainer").className;
    product.forEach(val => {
      if (val.title.trim().toLowerCase() === name.trim().toLowerCase()) {
        val.cart=0;
        setArr([...product]);
      }
    })
  }

  return (
    <>
    <div id="containerAll">

    </div>
      <div id='itemContainer'>
        {arr.map((val) => {
          if (val.cart >= 1)
            return (
              <div id='cartContainer' className={val.title}>
                <div className="cartitemImg">
                  <img src={require(`../Images/${val.filename}`)} alt={val.title} />
                  <div><button onClick={sub} className="minus">-</button><input placeholder={val.cart} disabled /><button onClick={add}  className="plus">+</button></div>
                </div>
                < div id='itemDetails' >
                  <h3>{val.title}</h3>
                  <p>{val.description}</p>
                  <h5>Price : â‚¹{(val.price * val.cart).toFixed(1)}</h5>
                  <div className="extra" onClick={remove}><h3>Remove</h3></div>
                </div >
              </div>
            )
        })}
      </div>
      {/* <div id='placeOrder'><button>Place Order</button></div> */}
    </>
  )
}

export default Cart
