          {food.map((val) => {
            return <><div className="box">
              <img src="https://www.thespruceeats.com/thmb/yLyKgqIj6-SLt9gJkgkjfIZtfC0=/4300x2867/filters:no_upscale():max_bytes(150000):strip_icc()/chole-chickpea-curry-1957946-hero-01-cddf957cc78d4032ba29ec2adbc05a97.jpg" alt="product_image" />
              <h3>{val.title} </h3>
              <div className="price">₹{val.price} <span>₹{Number(val.price) + 50}</span></div>
              <button className="btn" onClick={addToCart}> Add to Cart</button>
            </div></>
          })}
        </div>
      </section>
      <Outlet />
    </>
  )
}

export default Offer
